package p2022

class Day6 {
    fun part1(input: String): Int {
        val result = input.windowed(4, 1).first { it.isunique() }
        return input.indexOf(result) + 4
    }

    fun part2(input: String): Int {
        val result = input.windowed(14, 1).first { it.isunique() }
        return input.indexOf(result) + 14
    }

    private fun String.isunique() = this.toList().distinct().size == this.length

}

fun main() {
    val d = Day6();
    println(d.part1("bvwbjplbgvbhsrlpgdmjqwftvncz"))
    println(d.part1("mjqjpqmgbljsphdztnvjfqwrcgsmlb"))
    println(d.part1("nppdvjthqldpwncqszvftbrmjlhg"))
    println(d.part1("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"))

    println(
        d.part1(
            ""
        )
    )

    println(d.part2("mjqjpqmgbljsphdztnvjfqwrcgsmlb"))
    println(d.part2("bvwbjplbgvbhsrlpgdmjqwftvncz"))
    println(d.part2("nppdvjthqldpwncqszvftbrmjlhg"))
    println(d.part2("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"))
    println(
        d.part2(
            ""
        )
    )
}
